import styled from 'styled-components'
import {NavLink as Link} from 'react-router-dom'


export const ToggleNavContainer = styled.div`
    position:fixed;
    z-index:1;
    width:100%;
    height:100%;
    background:#111111;
    display:grid;
    align-items:center;
    top:0;
    left:0;
    transition: 0.3s ease-in-out;
    opacity:${({isOpen}) => (isOpen ? '100%' : '0')};
    top:${({isOpen}) => (isOpen ? '0' : '-100%')};
`

export const Icon = styled.div`
    position:absolute;
    top:1.2rem;
    text-align:center;
    right:1.5rem;
    background:transparent;
    font-size:2rem;
    cursor:pointer;
    outline:none;
`

export const ToggleNavMenu = styled.div`
    display:block;
    position:relative;
    top:0;
    left:0;
    `
    

export const ToggleNavLink = styled(Link)`
    color:#fff;
    display:flex;
    align-items:center;
    text-decoration:none;
    padding:0 1rem;
    height:100%;
    cursor:pointer;
`
