import styled from 'styled-components'
import {NavLink as Link} from 'react-router-dom'
import {FaTimes} from 'react-icons/fa'

export const Icon = styled.div`
    position:absolute;
    top:1.2rem;
    

`

export const CloseIcon = styled(FaTimes)`

`

export const ToggleNavContainer = styled.container`
    display:block;
    position:absolute;
    background-color:#1111111;
`

export const ToggleNavMenu = styled.div`
    display:block;
    position:relative;
    top:0;
    left:0;
`

export const ToggleNavLink = styled(Link)`
    color:#fff;
    display:flex;
    align-items:center;
    text-decoration:none;
    padding:0 1rem;
    height:100%;
    cursor:pointer;
`
