import styled from 'styled-components'
import {Link as LinkR} from 'react-router-dom'
// import {Link as LinkS} from 'react-scroll'


export const Nav = styled.nav`
    background : #111111;
    height:80px;
    margin-top:-80px;
    position:sticky;
    display:flex;
    justify-content:center;
    align-items:center;
    font-size:1rem;
    top:0;
    z-index:10;

    @media screen and (max-width:960px){
        transition: 0.8s all ease;
    }
`

export const NavbarContainer = styled.div`
    display:flex;
    justify-content:space-between;
    height:80px;
    z-index:1;
    width:100%;
    max-width:1100px;
`
export const NavLogo = styled(LinkR)`
    color:#1399f2;
    display:flex;
    align-items:center;
    justify-self:flex-start;
    margin-left:24px;
    padding:0 1rem;
    height:100%;
    text-decoration:none;
    font-weight:bold;
    cursor:pointer;
`

export const MobileIcon = styled.div`
    display:none;
    color:#fff;
    
    @media screen and (max-width:768px){
        // display:block;
        position:absolute;
        top:0;
        right:0;
        transform:translate(-100%, 75%);
        font-size:1.8rem;
        dsiplay:${({isOpen}) => (isOpen ? 'none' : '0')};
        cursor:pointer;
    }
`

export const NavMenu = styled.ul`
    display:flex;
    justify-content:center;
    align-items:center;
    list-style:none;
    width:30%;


    @media screen and (max-width:768px){
        display:none;
    }
`

export const NavItem =styled.li`
    height:80px;
    display:flex;
    justify-content:space-between;
    align-items:center;

    &::after{
        content: '';
        width: 0;
        height: 2px;
        background:white ;
        display: block;
        margin: auto;
        // margin-top: 10px;
        -webkit-transition: .3s;
        transition: .3s;
    }

    &:hover::after{
        width: 50%;
    }
`

export const NavLink = styled(LinkR)`
    color:#fff;
    display:flex;
    align-items:center;
    text-decoration:none;
    padding:0 1rem;
    height:100%;
    cursor:pointer;

    &.active{
        border-bottom:3px solid #1399f2;
    }

    &:hover{
        color:#7af6ff;
    }
`
